<div class="container findform"><?= form_open(get_currcontroller()."/vfind/go/mrid/".get_curririd(), array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-4">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-4">	<?=form_label(lang('CODE'), 'code')?>	<br>	<?=form_input('code', element('_aircompanies.code', $search, ''), 'id="code" class="text" style="width: 30px;"')?></div><div class="column span-7">	<?=form_label(lang('NAME'), 'name')?>	<br>	<?=form_input('name', element('_aircompanies.name', $search, ''), 'id="name" class="text"  style="width: 150px;"')?></div><div class="column span-9 last">	<?=form_label(lang('IATA'), 'iata')?>	<br>	<?=form_input('iata', element('_aircompanies.iata', $search, ''), 'id="iata" class="text" style="width: 30px;"')?></div><?= form_close(); ?></div><div class="container" style="padding: 0px 0px 5px 0px; margin: 0px; text-align: right;">	<div class="column span-24 last">		<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset">	</div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#code').val('');					$('#name').val('');					$('#iata').val('');					$('#find_<?=$orid?>').submit();				});		})	</script>