umn span-3">	<?=form_label(lang('ISSUE'), 'issue')?> <font color="red">*</font></div><div class="column span-21 last">	<?=form_dropdown('issue', get_airissues_types(), set_value('issue', ''), 'id="issue" class="text"')?></div><div class="column span-24" id="routes">	<?=$routes?></div><div class="column span-3">	<?=form_label(lang('AIRCOMPANY'), '_aircompanies_rid')?> <font color="red">*</font></div><div class="column span-9">	<?=get_aircompanies_vp(set_value('_aircompanies_rid', ''))?></div><div class="column span-3">	<?=form_label(lang('AIRCLASS'), 'air_class')?> <font color="red">*</font></div><div class="column span-9 last">	<?=form_dropdown('air_class', get_airclasses(), set_value('air_class', ''), 'id="air_class" class="text" style="width:150px;"')?></div><div class="column span-12">	<b><?=lang('AREA_FROM')?></b></div><div class="column span-12 last">	<b><?=lang('AREA_TO')?></b></div><div class="column span-3">	<?=form_label(lang('COUNTRY_FROM'), '_countries_rid_from')?> <font color="red">*</font></div><div class="column span-9">	<?=form_dropdown('_countries_rid_from', get_countries_list(), set_value('_countries_rid_from', ''), 'id="_countries_rid_from" class="text" ')?></div><div class="column span-3">	<?=form_label(lang('COUNTRY_TO'), '_countries_rid_to')?> <font color="red">*</font></div><div class="column span-9 last">	<?=form_dropdown('_countries_rid_to', get_countries_list(), set_value('_countries_rid_to', ''), 'id="_countries_rid_to" class="text" ')?></div><div class="column span-3">	<?=form_label(lang('POINT_FROM'), 'point_from')?> <font color="red">*</font></div><div class="column span-9">	<?=form_input('point_from', set_value('point_from', ''), 'id="point_from" class="text" ')?></div><div class="column span-3">	<?=form_label(lang('POINT_TO'), 'point_to')?> <font color="red">*</font></div><div class="column span-9 last">	<?=form_input('point_to', set_value('point_to', ''), 'id="point_to" class="text