</div>
<div class="column span-24 last">	<input type="submit" value="<?=lang('SAVE')?>" class="button" id="submit" name="submit"> <input type="reset" value="<?=lang('CANCEL')?>" class="button" onclick="window.location='<?=site_url(get_currcontroller().'/mrid/'.get_curririd()) ?>';" id="reset" name="reset"></div><?= form_close(); ?></div><script type="text/javascript">function add_route(){	var query_string = '';	query_string = 'add_route=yes&'+$("input[name='_aircompanies_rid']").serialize()+'&'+					$("#air_class").serialize()+'&'+					$("#_countries_rid_from").serialize()+'&'+					$("#_countries_rid_to").serialize()+'&'+					$("#point_from").serialize()+'&'+					$("#point_to").serialize()+'&'+					$("#departure").serialize()+'&'+					$("#arrival").serialize()+'&'+					$("input[name='_aircompanies_rids[]']").serialize()+'&'+					$("input[name='air_classes[]']").serialize()+'&'+					$("input[name='_countries_rids_from[]']").serialize()+'&'+					$("input[name='_countries_rids_to[]']").serialize()+'&'+					$("input[name='points_from[]']").serialize()+'&'+					$("input[name='points_to[]']").serialize()+'&'+					$("input[name='departures[]']").serialize()+'&'+					$("input[name='arrivals[]']").serialize();	$.ajax({		type: 'POST',		url: "<?=site_url(get_currcontroller()."/getroutes/go/mrid/".get_curririd())?>",		data: query_string,		success: function(html){		    $('#routes').html(html);		}	});}$(document).ready(		function(){			$('#add_route_row').click(function(){				add_route();			});		})	</script>