<div class="container findform"><?= form_open(get_currcontroller()."/find/go/mrid/".get_curririd(), array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-4">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-10">	<?=form_label(lang('ITEM_NAME'), 'item_name')?>	<br>	<?=form_input('item_name', element('_menu_items.item_name', $search, ''), 'id="item_name" class="text"')?></div><div class="column span-10 last">	<?=form_label(lang('ITEM_CONTROLLER'), 'item_controller')?>	<br>	<?=form_input('item_controller', element('_menu_items.item_controller', $search, ''), 'id="item_controller" class="text"')?></div><?= form_close(); ?></div><div class="container" style="padding: 0px 0px 5px 0px; margin: 0px; text-align: right;">	<div class="column span-24 last">		<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset">	</div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#item_name').val('');					$('#item_controller').val('');					$('#find_<?=$orid?>').submit();				});		})	</script>
