<div class="grid"><h3><?=$title?></h3><?= form_open(get_currcontroller()."/create/go/mrid/".get_curririd(), array('id'=>'create_'.$orid, 'autocomplete'=>'off'))?><div class="container editform"><?if(validation_errors()){?><div class="error">	<?=validation_errors('<div>', '</div>');?></div>	<?}?><?if($success===False){?><div class="error">	<?=lang('SAVE_SYSTEM_ERROR')?></div><?}?><?if($success===True){?><div class="success">	<?=lang('SAVE_SYSTEM_SUCCESS')?></div><?}?><div class="column span-4">	<?=form_label(lang('POSITION'), '_positions_rid')?> <font color="red">*</font></div><div class="column span-8">	<?=form_dropdown('_positions_rid', get_positions_list(), set_value('_positions_rid', ''), 'id="_positions_rid" class="text" ')?></div><div class="column span-4">	<?=form_label(lang('MENU_ITEM'), '_menu_items_rid')?> <font color="red">*</font></div><div class="column span-8 last">	<?=form_dropdown('_menu_items_rid', get_menuitems_list(), set_value('_menu_items_rid', ''), 'id="_menu_items_rid" class="text" style="width:150px;"')?></div><div class="column span-4">	<?=form_label(lang('PARENT'), 'parent')?></div><div class="column span-20 last" id="menu_tree">	<?=build_tree_dropdown(set_value('_positions_rid', ''))?></div><div class="column span-4">	<?=form_label(lang('AREA'), 'item_area')?> <font color="red">*</font></div><div class="column span-20 last">	<?=form_dropdown('item_area', get_areas(), set_value('item_area', ''), 'id="item_area" class="text" ')?></div><fieldset>	<legend><?=lang('RIGHTS')?> <font color="red">*</font></legend>	<?=form_checkbox('item_rights[]', 'N', set_value('item_rights[]', '')=='N', 'id="item_rights[]"')?> <?=lang('R_N')?><br>	<?=form_checkbox('item_rights[]', 'E', set_value('item_rights[]', '')=='E', 'id="item_rights[]"')?> <?=lang('R_E')?><br>	<?=form_checkbox('item_rights[]', 'D', set_value('item_rights[]', '')=='D', 'id="item_rights[]"')?> <?=lang('R_D')?><br>	<?=form_checkbox('item_rights[]', 'R', set_value('item_rights[]', '')=='R', 'id="item_rights[]"')?> <?=lang('R_R')?><br>	<?=form_checkbox('item_rights[]', 'M', set_value('item_rights[]', '')=='M', 'id="item_rights[]"')?> <?=lang('R_M')?><br>	<?=form_checkbox('item_rights[]', 'A', set_value('item_rights[]', '')=='A', 'id="item_rights[]"')?> <?=lang('R_A')?><br></fieldset><div class="column span-4">	<?=form_label(lang('ORDER'), 'item_order')?></div><div class="column span-8">	<?=form_input('item_order', set_value('item_order', ''), 'id="item_order" class="text" style="width: 40px;"')?></div><div class="column span-4">	<?=form_label(lang('HIDDEN'), 'hidden')?></div><div class="column span-8 last">	<?=form_dropdown('hidden', array('0'=>lang('NO'), '1'=>lang('YES')), set_value('hidden', '0'), 'id="hidden" class="text" ')?></div><div class="column span-4">	<?=form_label(lang('DESCR'), 'descr')?></div><div class="column span-8">	<?=form_textarea('descr', set_value('descr', ''), 'id="descr" class="text" style="width:200px; height: 50px;"')?></div><div class="column span-4">	<?=form_label(lang('ARCHIVE'), 'archive')?></div><div class="column span-8 last">	<?=form_dropdown('archive', array('0'=>lang('NO'), '1'=>lang('YES')), set_value('archive', ''), 'id="archive" class="text" ')?></div>
</div>
<div class="column span-24 last">	<input type="submit" value="<?=lang('SAVE')?>" class="button" id="submit" name="submit"> <input type="reset" value="<?=lang('CANCEL')?>" class="button" onclick="window.location='<?=site_url(get_currcontroller().'/mrid/'.get_curririd()) ?>';" id="reset" name="reset"></div><?= form_close(); ?></div><script type="text/javascript" >	$(document).ready(function(){		$('#_positions_rid').change(function(){				var prid = $('#_positions_rid').val(); 				$.ajax({					type: 'POST',					data: ({prid : prid}),					url: "<?=site_url(get_currcontroller().'/mlist/go/mrid/'.get_curririd()) ?>",					 success: function(content){							    $("#menu_tree").html(content);							  }				});			});	})</script>