<div class="container findform"><?= form_open(get_currcontroller()."/find/go/mrid/".get_curririd(), array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-3">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-7">	<?=form_label(lang('SNAME'), 'stouroperator_name')?>	<br>	<?=form_input('stouroperator_name', element('_touroperators.stouroperator_name', $search, ''), 'id="stouroperator_name" class="text" style="width: 100px;"')?></div><div class="column span-7">	<?=form_label(lang('NAME'), 'touroperator_name')?>	<br>	<?=form_input('touroperator_name', element('_touroperators.touroperator_name', $search, ''), 'id="touroperator_name" class="text" style="width: 200px;"')?></div><div class="column span-7 last">	<?=form_label(lang('CATEGORY'), 'category')?>	<br>	<?=form_dropdown('category', get_tocats(), element('_touroperators.category', $search, ''), 'id="category" class="text"')?></div><div class="column span-3">	&nbsp;</div><div class="column span-7">	<?=form_label(lang('CONTRACT'), 'contract')?>	<br>	<?=form_input('contract', element('_touroperators.contract', $search, ''), 'id="contract" class="text" style="width: 100px;"')?></div><div class="column span-7">	<?=form_label(lang('CONTRACT_PERIOD_FROM'), 'contract_period_from')?>	<br>	<?=form_input('contract_period_from', element('_touroperators.contract_period >=', $search, null), 'id="contract_period_from" class="text" style="width: 90px;" readonly="readonly"')?>	<script type="text/javascript">		$('#contract_period_from').datepick({showOn: 'button', 	    buttonImageOnly: true, buttonImage: '<?=base_url()?>public/js/jquery.datapick.package-3.6.1/calendar.gif'});	</script>	</div><div class="column span-7 last">	<?=form_label(lang('CONTRACT_PERIOD_TO'), 'contract_period_to')?>	<br>	<?=form_input('contract_period_to', element('_touroperators.contract_period <=', $search, null), 'id="contract_period_to" class="text" style="width: 90px;" readonly="readonly"')?>	<script type="text/javascript">		$('#contract_period_to').datepick({showOn: 'button', 	    buttonImageOnly: true, buttonImage: '<?=base_url()?>public/js/jquery.datapick.package-3.6.1/calendar.gif'});	</script></div><?= form_close(); ?></div><div class="container" style="padding: 0px 0px 5px 0px; margin: 0px; text-align: right;">	<div class="column span-24 last">		<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset">	</div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#stouroperator_name').val('');					$('#touroperator_name').val('');					$('#category').val('');					$('#contract').val('');					$('#contract_period_from').val('');					$('#contract_period_to').val('');					$('#find_<?=$orid?>').submit();				});		})	</script>
