<div class="popcats-block">	<div class="popcats-header">		<h3><?=lang('MOSTPOPULAR_MODULE_CATEGORIES_TITLE')?></h3>		<div class="popcats-right-corner"></div>	</div>	<div class="popcats-content">   		<?php foreach($cats as $row) { ?>   		<div class="popcats-item">   			<?=anchor('category/'.$row->rid.'-'.$row->slug, img(array('src'=>$row->img, 'alt'=>$row->name, 'border'=>0)), 'title="'.$row->name.'"')?>   			<h3><?=anchor('category/'.$row->rid.'-'.$row->slug, $row->name, 'title="'.$row->name.'"')?></h3>   			<div class="subcats">   			<?php $i=1; foreach($row->leafs as $subcat) { ?>   				<?=$i?>.&nbsp;<?=anchor('category/'.$subcat->rid.'-'.$subcat->slug, $subcat->name, 'title="'.$subcat->name.'"')?><br>   			<?php $i++; } ?>   			</div>		</div>		<?php } ?>		<div style="clear:both"></div>	</div>		<div class="popcats-bottom-border">		<div class="popcats-footer-bar">			<div class="popcats-bottom-right-corner"></div>		</div>	</div>	</div>	