<?php/* * The Accounts*/class Accounts extends Controller{	private $objectsArr = array();	public function __construct(){		parent::Controller();		$this->load->library('settings_module');		$this->load->library('search_module');		$this->load->library('accounts_module');		$this->load->library('navline_module');		$this->load->library('quickmenu_module');		$this->load->library('contacts_module');		$this->load->library('keywords_module');		/* generate objects */		$this->objectsArr['search_bar_obj'] = $this->search_module->RenderSearchBar();		$this->objectsArr['footermenu_area_obj'] = $this->quickmenu_module->RenderFootermenuArea();		$this->objectsArr['contactstoolbar_area_obj'] = $this->contacts_module->RenderContactsToolbar();		$this->objectsArr['metatitle_area_obj'] = $this->keywords_module->RenderMetatitleArea();		$this->objectsArr['metadescription_area_obj'] = $this->keywords_module->RenderMetadescriptionArea();		$this->objectsArr['keywords_area_obj'] = $this->keywords_module->RenderKeywordsArea('');		/* load needed libraries */		}		public function index(){		$this->objectsArr['account_area_obj'] = $this->accounts_module->loginProcessing();		$this->load->view('layouts/accounts/accounts.php', $this->objectsArr);	}		public function login(){		$this->objectsArr['account_area_obj'] = $this->accounts_module->loginProcessing();		$this->load->view('layouts/accounts/accounts.php', $this->objectsArr);			}	public function checkLogin($login){		return $this->accounts_module->checkLogin($login);		}		public function logout(){		$this->accounts_module->logout();		redirect('accounts', 'refresh');	}	public function register(){		$this->objectsArr['account_area_obj'] = $this->accounts_module->registerProcessing();		$this->load->view('layouts/accounts/accounts.php', $this->objectsArr);			}	public function chpass(){		$this->objectsArr['account_area_obj'] = $this->accounts_module->changePass();		$this->load->view('layouts/accounts/accounts.php', $this->objectsArr);			}	public function restorepass(){		$this->objectsArr['account_area_obj'] = $this->accounts_module->restorePass();		$this->load->view('layouts/accounts/accounts.php', $this->objectsArr);			}		public function reloadcaptcha(){		$this->output->enable_profiler(False);		$this->output->set_output($this->accounts_module->getCaptcha());		}		public function isUniqueLogin($login){		return $this->accounts_module->isUniqueLogin($login);	}	public function isUniqueEmail($email){		return $this->accounts_module->isUniqueEmail($email);	}	public function isEmailExist($email){		return $this->accounts_module->isEmailExist($email);	}		public function checkCaptcha($captcha){		return $this->accounts_module->checkCaptcha($captcha);	}}?>