<?php/* * The settings*/class Settings extends Controller{	private $objectsArr = array();
	public function __construct(){		parent::Controller();		$this->output->enable_profiler(TRUE);		/* load needed libraries */			$this->load->library('navline_module');		$this->load->library('categories_module');		$this->load->library('search_module');		$this->load->library('clients_module');		$this->load->library('keywords_module');		$this->load->library('settings_module');		/* generate objects */		$this->objectsArr['nav_top_obj'] = $this->navline_module->RenderTopMenu();		$this->objectsArr['topmenu_obj'] = $this->categories_module->renderTabbedMenu();		$this->objectsArr['search_bar_obj'] = $this->search_module->renderNarrowBar();		$this->objectsArr['footer_area_obj'] = $this->navline_module->RenderFooterMenu();		$this->objectsArr['metatitle_area_obj'] = $this->keywords_module->RenderMetatitleArea();		$this->objectsArr['keywords_area_obj'] = $this->keywords_module->RenderKeywordsArea('');		$this->objectsArr['metadescription_area_obj'] = $this->keywords_module->RenderMetadescriptionArea();	}
	public function index(){		$this->objectsArr['settings_area_obj'] = $this->settings_module->renderSetSettingsArea();		$this->load->view('layouts/settings/settings.php', $this->objectsArr);	}
	public function changecountry(){		$this->output->enable_profiler(FALSE);		$this->output->set_output($this->settings_module->changeCountry());	}
	public function changeregion(){		$this->output->enable_profiler(FALSE);		$this->output->set_output($this->settings_module->changeRegion());	}}
?>