<?php/** * Welcome class - welcome controller * The first controller of saleinform  which loads by default *  * @access public * @author Mazvv * @package controllers * @copyright Copyright 2007 (c) */class Welcome extends Controller{	private $objectsArr = array();	/**	 * Welcome::__construct. Initialize welcome controller	 * 	 * @access public	 * @author MAzvv	 * @param void	 * @return void 	 */	function __construct(){		parent::Controller();
		// { Enable profiler for admin only
		$this->output->enable_profiler(True);		
		// } Enable profiler for admin only		/* load needed libraries */			$this->load->library('navline_module');		$this->load->library('search_module');		$this->load->library('categories_module');		$this->load->library('info_module');		$this->load->library('clients_module');		$this->load->library('mostpopular_module');		$this->load->library('last_module');		/* generate objects */		$this->objectsArr['nav_top_obj'] = $this->navline_module->renderMainTopMenu();		$this->objectsArr['search_bar_obj'] = $this->search_module->renderSearchBar();		$this->objectsArr['categories_top_obj'] = $this->categories_module->renderTopCategories();		$this->objectsArr['settings_area_obj'] = $this->settings_module->renderSettingsArea();		$this->objectsArr['newest_clients_obj'] = $this->clients_module->renderNewestClients();		$this->objectsArr['whatisit_area_obj'] = $this->info_module->renderWhatIsItArea();		$this->objectsArr['cloud_area_obj'] = $this->mostpopular_module->renderSearchesTags();		$this->objectsArr['lastnews_area_obj'] = $this->last_module->renderLastNewsArea();		$this->objectsArr['footer_area_obj'] = $this->navline_module->renderFooterMenu();		$this->objectsArr['keywords_area_obj'] = $this->keywords_module->renderKeywordsArea('');		$this->objectsArr['metatitle_area_obj'] = $this->keywords_module->renderMetatitleArea();		$this->objectsArr['metadescription_area_obj'] = $this->keywords_module->renderMetadescriptionArea();	}
	
	/**	 * Welcome::index - default controller's method	 * 	 * @access public	 * @author Mazvv	 * @param void	 * @return void	 */	function index(){		$this->load->view('layouts/welcome.php', $this->objectsArr);	}
}
?>