<?php/* * Linkchanges Model */class Linkchanges_model extends Model{	public function __construct(){		parent::Model();	}
	public function GetLinksArr($links_quan = 10, $catRID = null){		$this->db->select('_links.*');		$this->db->from('_links');		if($catRID){			$this->db->join("_linkstocategories", "_linkstocategories._links_rid = _links.rid AND _linkstocategories.archive = 0 AND _linkstocategories._categories_rid = $catRID");		}		$this->db->orderby("RAND()");		$this->db->limit("$links_quan");		$query = $this->db->get();		return $query->num_rows()?$query->result():array();	}
}
?>