ls/bar_0.gif', 'id'=>"i{$row->rid}"))?>				<? } else if($row->r_rate >= 50) { ?>					<?=img(array('src'=>'images/ratings/helpfulls/bar_50.gif', 'id'=>"i{$row->rid}"))?>				<? } else { ?>					<?=img(array('src'=>'images/ratings/helpfulls/bar_'.$row->r_rate.'.gif', 'id'=>"i{$row->rid}"))?>				<? } ?>				<br>				<?=$row->rdate?>				<br>				<?if($user) { ?>				<div id="r<?=$row->rid?>">					<?if(!$row->urate) { ?>						<a href="javascript:void(0);" onClick="rateReview(<?=$row->rid?>, 1)" id="a<?=$row->rid?>_plus" title="+1"><?=img(array('src'=>'images/prodpage_i_like_it.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?></a>						<a href="javascript:void(0);" onClick="rateReview(<?=$row->rid?>, -1)" id="a<?=$row->rid?>_minus" title="-1"><?=img(array('src'=>'images/prodpage_i_dislike_it.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?></a>					<? } else { ?>						<?if($row->urate>0) { ?>							<?=img(array('src'=>'images/prodpage_i_like_it_active.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?>							<?=img(array('src'=>'images/prodpage_i_dislike_it.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?>						<? } else { ?>							<?=img(array('src'=>'images/prodpage_i_like_it.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?>							<?=img(array('src'=>'images/prodpage_i_dislike_it_active.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?>						<? } ?>					<? } ?>				</div>				<? } ?>							</td>		</tr>		<? } ?>	</tbody></table><br><?=anchor('clients/editreview/'.$client_info->rid.'-'.$client_info->slug, lang('CLIENTS_WRITE_REVIEW'), 'class="btnR"')?><div class="ptab4onbd" id="prodNav"><ul>	<li id="ptab1on">		<a name="top">			<h4><span><?=lang('CLIENTS_RESOURCES')?></span></h4>		</a>	</li></ul></div><table cellspacing="0" class="resultrangetopPriceComp2 bg3boxgdt">	<tr>		<td class="rangearticles"> </td>		<td class="rangepages"> </td>		<td class="rangenextpage"/>	</tr></table><script type="text/javascript">function rateReview(review, rate){	$.ajax({		  type: "POST",		  url: "<?=base_url().index_page()?>/clients/reviewrate",		  data: {review: review, rate: rate},		  success: function(currRate){		    var r = parseInt(currRate);		    var newImage = '';		    if(r<=0) newImage = '<?=base_url()?>images/ratings/helpfulls/bar_0.gif';		    else if(r>=50) newImage = '<?=base_url()?>images/ratings/helpfulls/bar_50.gif';		    else newImage = '<?=base_url()?>images/ratings/helpfulls/bar_'+r+'.gif';		    $('#i'+review).attr('src', newImage);		    $('#a'+review+'_plus').remove();		    $('#a'+review+'_minus').remove();		    if(rate>0){		    	$('#r'+review).append('<?=img(array('src'=>'images/prodpage_i_like_it_active.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?>&nbsp;'); 				$('#r'+review).append('<?=img(array('src'=>'images/prodpage_i_dislike_it.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?>');		 	} else {		    	$('#r'+review).append('<?=img(array('src'=>'images/prodpage_i_like_it.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?>&nbsp;'); 				$('#r'+review).append('<?=img(array('src'=>'images/prodpage_i_dislike_it_active.gif', 'width'=>"26", 'height'=>"23", 'border'=>"0"))?>');		 	}		  } 		});	}</script>